import React, { useState } from "react";
import { logo } from "../assets/images";
import { Link } from "react-router-dom";
import { ArrowDropDownIcon } from "../assets/icons";

const Header = () => {

  const  [drop,setDrop] = useState(false);

  return (
    <header className="w-full bg-green-100 px-4 py-4 flex items-center justify-between">
      <Link to={"/"}>
        <img src={logo} alt="" className="w-24 object-cover" />
      </Link>
      <div className="w-[40%] flex bg-white">
        <div className="relative bg-gray-500 px-4 py-2 items-center w-auto">
          <div onClick={()=>setDrop(!drop)} className="flex items-center justify-center">
            <span className="text-xs">  All  </span>
            <ArrowDropDownIcon />
          </div>
          {drop ? <ul className="absolute h-60 overflow-y-auto w-80 bg-white p-1 top-11 left-0 border-2 border-black">
            <li className="bg-blue-500">Amazon Datalink</li>
          </ul> : null}
        </div>

        <input type="text" className="block w-full" />
        <span></span>
      </div>
      <nav>
        <Link to={"/"}></Link>
      </nav>
    </header>
  );
};

export default Header;
