import React, { useContext } from 'react'
import { Link } from "react-router-dom";
import { UserContext } from '../components/UserContext';

const ProfilePage = () => {
  const {userInfo,setUserInfo} = useContext(UserContext)

  function logout() {
      fetch("http://localhost:4000/logout", {
        credentials: "include",
        method: "POST",
      });
      setUserInfo(null);
    }
  return (
    <section className="max-container my-10 w-1/2">
      <div className="bg-orange-500 rounded-lg p-10 flex flex-col">

        <h1 className='text-white font-bold'>Username: {userInfo.username}</h1>
        <h2 className='text-white font-semibold'>id: {userInfo.id}</h2>
        <Link onClick={logout} to={"/"} className='text-white font-semibold'>
          Logout
        </Link>
      </div>
      <div>

      </div>
    </section>
  );
};

export default ProfilePage;
