import React from "react";
import { featured1 } from "../assets/images/products";
import { FavoriteBorderIcon, StarRoundedIcon } from "../assets/icons";
const ProductCard = () => {
  return (
    <div className="h-[300px] w-[200px] border-slate-200 rounded-md p-2 border-[1px] mx-auto shadow-lg">
      <div className="w-full  relative bg-orange-100">
        <img src={featured1} className="h-full p-1 " alt="" />
        <div className="absolute bottom-3 px-1 left-2  bg-yellow-50 opacity-75  py-1 rounded-sm flex items-center gap-2 border-[0.5px] border-orange-500">
          <StarRoundedIcon />
          <h4>4.7</h4>
        </div>
        <div className=" absolute bottom-3 right-2  rounded-full p-1 border-[0.5px] bg-yellow-50 opacity-75 border-orange-500   text-center">
          <FavoriteBorderIcon />
        </div>
      </div>
      <h5 className="text-lg font-semibold pt-5">Controller</h5>
      <p className="text-sm pt-1">Kids Teen Controller</p>
      <div className="flex items-center gap-1 ">
        <p className="font-semibold">₹1,199</p>
        <p className="line-through text-xs">₹1,399</p>
      </div>
    </div>
  );
};

export default ProductCard;
