import React,{useState} from "react";
import { Link } from "react-router-dom";
import { Navigate } from "react-router-dom";
import { auth } from "../assets/images/banner";
// import { GoogleOAuthProvider, GoogleLogin } from "@react-oauth/google";
// import jwtdecode from 'jwt-decode';

const LoginPage = () => {
  // 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
  //   apikey AIzaSyDLiFPNW9LQOrhWPacgdjTt5fD-xVf_LBc

  const [username,setUsername] = useState('');
  const [password,setPassword] = useState('');
  const [redirect,setRedirect] = useState(false);
  // const {setUserInfo} = useContext(UserContext);
  async function login(ev) {
    ev.preventDefault();
    const response = await fetch('http://localhost:4000/login', {
      method: 'POST',
      body: JSON.stringify({username, password}),
      headers: {'Content-Type':'application/json'},
      credentials: 'include',
    });
    if (response.ok) {
      response.json().then(userInfo => {
        // setUserInfo(userInfo);
        setRedirect(true);
      });
    } else {
      alert('wrong credentials');
    }
  }

  if (redirect) {
    return <Navigate to={'/'} />
  }

  return (
    <section className={`max-container h-96 my-12 flex justify-between`} >
      <div className="">
          <img src={auth} alt="" />
      </div>
      <div className="w-[40%] flex flex-col gap-4">
      <h1 className="text-xl font-bold text-orange-500 text-center mb-10">Login</h1>

      <form action="" className="flex flex-col gap-5" onSubmit={login}>
          <input
            type="text"
            placeholder="Username"
            className="input"
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            className="input"
            onChange={(e) => setPassword(e.target.value)}
          />
          <button className="p-2 bg-orange-500 rounded-lg text-white font-semibold shadow-lg">
            Login
          </button>
        </form>
      <p className="text-center"><span className="text-sm font-medium">Don't have any account? <Link to={'/auth/signup'} className="font-semibold text-orange-500">Sign up</Link></span></p>
      </div>
    </section>
  );
};

export default LoginPage;

