import React from "react";
import { featured1 } from "../assets/images/products";
import { FavoriteBorderIcon, star } from "../assets/icons";
const ProductCard = () => {
  return (
    <div className="border-slate-200 rounded-md max-md:p-1 p-2 border-[1px] mx-auto shadow-sm hover:ease-out hover:duration-700 hover:shadow-lg">
      <div className="w-full  relative bg-orange-100">
        <img src={featured1} className="h-full p-1 " alt="" />
        <div className="absolute bottom-3  left-2  bg-yellow-50 opacity-75  rounded-sm flex items-center gap-2 border-[0.5px] border-orange-500">
          <img src={star} alt="" className="max-lg:w-3" />
          <h4 className="max-md:text-xs">4.7</h4>
        </div>
        <div className=" absolute bottom-3 right-2  rounded-full p-1 border-[0.5px] bg-yellow-50 opacity-75 border-orange-500   text-center">
          <FavoriteBorderIcon />
        </div>
      </div>
      <h1 className="text-lg font-semibold pt-5 max-md:pt-2 max-md:text-sm max-sm:text-[10px]">Controller</h1>
      <p className="text-sm max-md:text-xs max-sm:text-[8px]">Kids Teen Controller</p>
      <div className="flex items-center gap-1 max-md:text-[8px]">
        <p className="font-semibold">₹1,199</p>
        <p className="line-through ">₹1,399</p>
      </div>
    </div>
  );
};

export default ProductCard;
